import { LOGIN_USER_FAIL,LOGIN_USER_SUCCESS,LOGIN_USER_REQUEST,LOGOUT_FAIL,LOGOUT_SUCCESS } from "../constants/userConstants"
import { REGISTER_USER_FAIL,REGISTER_USER_SUCCESS,REGISTER_USER_REQUEST,CLEAR_ERRORS} from "../constants/userConstants"
export const userReducer = (state={user:{}},action) => {
   switch(action.type){
    case LOGIN_USER_REQUEST:
    case REGISTER_USER_REQUEST:
       return {...state,
        loading:true,
        isAuthenticated:false
    }
    case LOGIN_USER_SUCCESS:
    case REGISTER_USER_SUCCESS:
        return {...state,
            loading:false,
            isAuthenticated:true
        }
    case LOGIN_USER_FAIL:
    case REGISTER_USER_FAIL:
        return {...state,
            loading:false,
            isAuthenticated:false,
            user:null,
            error:action.payload}
    case LOGOUT_SUCCESS:
        return {
            loading:false,
            isAuthenticated: false,
            user : null,
        }
    case LOGOUT_FAIL:
        return {
            ...state,
            error: action.payload
        }     
    case CLEAR_ERRORS:
        return {...state,error:null}
    default:
        return state;
   }
}
