import { LOGIN_USER_REQUEST, LOGIN_USER_FAIL, LOGIN_USER_SUCCESS } from "../constants/userConstants";
import { REGISTER_USER_FAIL, REGISTER_USER_SUCCESS, REGISTER_USER_REQUEST, CLEAR_ERRORS } from "../constants/userConstants"
export const login = (email, password) => async (dispatch) => {
    console.log("email", email);
    console.log("password", password);
    dispatch({
        type: LOGIN_USER_REQUEST
    })
    const config={
        headers:{
            'Content-Type':'application/json'
        }
      }
    const {data} = await axios.post("http://localhost:3001/user/login",{email,password},config)
}
export const clearErrors = () => async (dispatch) => {
    dispatch({
        type: CLEAR_ERRORS
    })
}
